import React from "%{ amd: 'react', common: '!React' }";
import ReactDOM from "%{ amd: 'react-dom', common: '!ReactDOM' }";

const span = document.createElement('span');
span.innerHTML = '<div id="example-react-in-js"></div>';
const children = span.children;
const len = children.length
let child, i;

for (i = 0; i < len; i++) {
    child = children[i];
    document.body.appendChild(child);
}

ReactDOM.render(<h1>Hello, world! React JSX</h1>, document.getElementById('example-react-in-js'));
